class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long size=nums.size();
        vector<int> anums(size),atarget(size);
        anums[0]=nums[0],atarget[0]=target[0];
        for(int i=1;i<size;i++)
            anums[i]=nums[i]-nums[i-1],atarget[i]=target[i]-target[i-1];

        long long ad=0,dd=0,d;
        for(int i=0;i<size;i++){
            d=atarget[i]-anums[i];
            if(d>0)
                ad+=d;
            else
                dd-=d;
        }
        return max(ad,dd);
    }
};
