class Solution:
    def minimumOperations(self, s: List[int], t: List[int]) -> int:
        a = c = 0
        for i, v in enumerate(s):
            v -= t[i]
            if v > 0:
                if v > a:
                    if a < 0:
                        a = 0
                    c += v - a
                a = v
            else:
                if v < a:
                    if a > 0:
                        a = 0
                    c += a - v
                a = v
        return c
