class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ret = 0;
        long long buf = 0;
        int pos = 0;
        int sz = nums.size();
        for (int i = 0; i < sz; i++) {
            int diff = nums[i] - target[i];
            if (diff == 0) {
                buf = 0;
                continue;
            }
            //cout<<"diff="<<diff<<", buf="<<buf<<", pos="<<pos<<", ret="<<ret<<endl;
            if (diff > 0) {
                if (pos != -1) {
                    if (diff > buf) {
                        ret += (diff-buf);
                        buf = diff;
                    } else {
                        buf = diff;
                    }
                } else {
                    buf = diff;
                    ret += buf;
                }
                pos = 1;
            } else {
                if (pos != 1) {
                    if (diff < buf) {
                        ret += (buf - diff);
                        buf = diff;
                    } else {
                        buf = diff;
                    }
                } else {
                    buf = diff;
                    ret -= diff;
                }
                pos = -1;
            }
        }
        return ret;
    }
};
