#define ll long long
#define pb push_back
class Solution {
public:
    ll f(vector<ll>& p){
        ll n = p.size();
        ll tot = p[0];
        for(ll i=1;i<n;i++){
            if(p[i]>p[i-1]){
                tot+=p[i]-p[i-1];
            }
        }
        return tot;
    }
    long long minimumOperations(vector<int>& n, vector<int>& t) {
        vector<ll>p1(n.size());
        vector<ll>p2(n.size());
        for(ll i=0;i<n.size();i++){
            if(n[i]>t[i]){
                p1[i]=n[i]-t[i];
            }else{
                p2[i]=t[i]-n[i];
            }
        }

        ll ans1 = f(p1);
        ll ans2 = f(p2);
        return ans1+ans2;
    }
};
