class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> change;
        for(int i = 0;i<target.size();i++)  change.push_back(target[i] - nums[i]);
        long long ans = 0;
        int prev = 0;
        bool flag = 1;
        for (int i = 0;i<change.size();i++){
            if (change[i]<0 && flag){
                flag  = 0;
                prev = 0;
            }
            else if (change[i]>0 && !flag){
                flag = 1;
                prev = 0;
            }
            if (abs(change[i])>prev){
                ans+=(abs(change[i]) - prev);
            }
            prev = abs(change[i]);
        }
        return ans;
    }
};
