class Solution {
public:
    int dx[8]={0,0,1,-1,1,1,-1,-1};
int dy[8]={1,-1,0,0,1,-1,-1,1};

const int N=2e5+1;
#define pii pair<int,int>
#define all(arr) arr.begin(),arr.end()
#define fr first
#define sc second
#define pb push_back
#define ll long long

    ll f(vector<ll>&arr){
        ll ans=0;
        // sort(all(arr));
        int plus=0;
        for(auto &x: arr){
            if(x>plus)ans+=x-plus;
            plus= x;
        }
        // for(auto &x: arr)cout<<x<<" ";
        // cout<<endl;
        // cout<<ans<<endl;
        return ans;
    }

    long long minimumOperations(vector<int>& arr, vector<int>& t) {
        ll ans=0;
        int i=0;
        int n= arr.size();
        while(i<n){
            while(i<n && arr[i]==t[i])i++;
            if(i>=n)break;

            vector<ll>curr;
            if(arr[i]>t[i]){
                while(i<n && arr[i]>t[i]){
                    curr.pb(arr[i]-t[i]);
                    i++;
                }
                if(curr.size()>0)
                ans+= f(curr);
                if(i>=n)break;
            }
            else{
                while(i<n && arr[i]<t[i]){
                    curr.pb(t[i]-arr[i]);
                    i++;
                }
                if(curr.size()>0)
                ans+= f(curr);
                if(i>=n)break;
            }
        }
        return ans;
    }
};
