class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> vec(n);
        for (int i = 0; i < n; i++) {
            vec[i] = target[i] - nums[i];
        }
        long long ans = 0;
        long long x = 0;
        for (int i = 0; i < n; i++) {
            if (i == 0 || (vec[i - 1] > 0 && vec[i] > 0) ||
                (vec[i - 1] < 0 && vec[i] < 0)) {
                int temp = abs(vec[i]) - abs(x);
                if (temp > 0) {
                    ans += temp;
                }
                x = vec[i];
            } else {
                ans += abs(vec[i]);
                x = vec[i];
            }
        }
        return ans;
    }
};
