class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> v(n,1);
        for (int i = 0; i < n; i++) {
            if (target[i] >= nums[i]) {
                nums[i] = target[i] - nums[i];
            } 
            else {
                v[i] = 0;  
                nums[i] = nums[i] - target[i];
            }
        }

        long long ans = nums[0];
        int num = nums[0];
        for (int i = 1; i < n; i++) {
            if (v[i] == v[i - 1]) {
                if (nums[i] >= num) {
                    ans += abs(nums[i] - num);
                    num = nums[i];
                } 
                else {
                     num = nums[i];
                    ans += 0;
                }
            } 
            else {
                ans += nums[i];
                num = nums[i];
            }
        }
        return ans;
    }
};
