class Solution {
private:
    bool samesign(int a, int b){
        if(a==0 || b== 0) return false;
        return (a>=0 && b>=0) || (a<=0) && (b<=0);
    }
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> difference(n);

        for(int i=0; i<n; i++){
            difference[i] = target[i] - nums[i];
        }

        int ind = 0;
        while(ind < n && difference[ind] == 0) ind++;
        if(ind >= n) return 0;
        int prevdiff = difference[ind];
        long long ans = abs(difference[ind]);

        for(int i=ind+1; i<n; i++){
            int currdiff = difference[i];
            if(samesign(prevdiff,currdiff)){
                if(abs(currdiff) > abs(prevdiff)){
                    ans += abs(currdiff-prevdiff);
                }
            }
            else{
                ans += abs(currdiff);
            }
            prevdiff = currdiff;
        }
        return ans;
    }
};
