class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0,dif=0;
        for(int i=0;i<nums.size();i++)
        {
            long long d=target[i]-nums[i];

            if((d*dif)<=0)
            ans+=abs(d),dif=d;
            else
            {
                if(dif>0)
                {
                    if(dif>d)
                    dif=d;
                    else
                    ans+=d-dif,dif=d;
                }
                else
                {
                    if(dif<d)
                    dif=d;
                    else
                    ans+=dif-d,dif=d;
                }
            }
            // cout<<ans<<" "<<dif<<endl;
        }
        return ans;
    }
};
