class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        long ans = 0;
        int[] diff = new int[n];
        for (int i = 0; i < n; i++) {
            diff[i] = target[i] - nums[i];
        }
        long currdiff = 0;
        for (int i = 0; i < n; i++) {
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                long extra = Math.abs(diff[i]) - Math.abs(currdiff);
                if (extra > 0) {
                    ans += extra;
                }
                currdiff = diff[i];
            } else {
                ans += Math.abs(diff[i]);
                currdiff = diff[i];
            }
        }
        return ans;
    }
}
