class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        index = 0

        current_nums = nums.copy()

        def increment(start, current_nums, diff):
            while start < len(current_nums) and current_nums[start] < target[start]:
                if (target[start] - current_nums[start]) < diff:
                    diff = (target[start] - current_nums[start])
                    current_nums[start] += diff
                else:
                    current_nums[start] += diff
                start += 1
            return current_nums

        def decrement(start, current_nums, diff):
            while start < len(current_nums) and current_nums[start] > target[start]:
                if (current_nums[start] - target[start]) < diff:
                    diff = (current_nums[start] - target[start])
                    current_nums[start] -= (current_nums[start] - target[start])
                else:
                    current_nums[start] -= diff
                start += 1
            return current_nums


        operations = 0

        while index < len(current_nums):
            if current_nums[index] == target[index]:
                index += 1
            elif current_nums[index] > target[index]:
                diff = current_nums[index] - target[index]
                current_nums = decrement(index, current_nums, diff)
                operations += diff
            else:
                diff = target[index] - current_nums[index]
                current_nums = increment(index, current_nums, diff)
                operations += diff

        return operations
