class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [x - y for x, y in zip(nums, target)] + [0]
        stk = [0]
        tot = 0
        for x in diff:
            last = stk[-1]
            while stk and stk[-1] > x:
                last = stk.pop()
            stk.append(x)
            tot += max(0, x - last)
        return tot
