class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        long long no=nums.size(),p=0,n=0;
        vector<long long>diff(no,0);
        for(int i=0;i<no;i++)
        {
              diff[i]=(target[i]-nums[i]);
        }
        for(int i=0;i<diff.size();i++)
        {
            if(diff[i]>0)
            {   n=0;
                if(diff[i]>p)
                {
                    ans+=diff[i]-p;
                    p=diff[i];
                }
                else
                    p=diff[i];

            }
            else if(diff[i]<0)
            {    p=0;
                    int val=abs(diff[i]);
                    if(val>n)
                    {
                          ans+=val-n;
                        n=val;
                    }
                  else
                      n=val;
            }
            else
            {
                p=0;
                n=0;
            }
        }
        return ans;
    }
};
