class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& tgt) {
        long long res=0;
        long long pichla=0;
        long long n=nums.size();
        for(int i=0;i<n;i++)
        {
            if(nums[i]==tgt[i])pichla=0;
            else if(nums[i]>tgt[i])
            {
                long long antrr=nums[i]-tgt[i];
                if(pichla<=0)res+=antrr;
                else if(antrr>pichla)
                {
                    res+=(antrr-pichla);
                }
                pichla=antrr;
            }
            else if(nums[i]<tgt[i])
            {
                long long antrr=nums[i]-tgt[i];
                if(pichla>=0)res-=antrr;
                else if(antrr<pichla)
                {
                    res+=(pichla-antrr);
                }
                pichla=antrr;
            }
        }
        return res;
    }
};
