#define ll long long


class Solution {
public:
    long long minimumOperations(vector<int>& v1, vector<int>& v2) {

vector<int> diff;

for(int i=0;i<v1.size();i++){
    diff.push_back(v2[i]-v1[i]);
}

ll ans=0;
ll len=0;

for(int i=0;i<diff.size();i++){

 ll d=diff[i];
   if(d>=0){


   if(len<0){
    ans+=d;
    len=d;
   }else{
    ans+=(d-min(len,d));
    len=min(len,d)+(d-min(len,d));
   }


   }else{

    if(len>=0){
      ans+=abs(d);
      len=d;
    }else{
        ans+=(abs(d)-min(abs(len),abs(d)));
        len=-(min(abs(len),abs(d))+(abs(d)-min(abs(len),abs(d))));
    }

   }
}



return ans;

    }
};
