class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        d = [a - b for a, b in zip(nums, target)]
        r = 0
        print(d)
        for _, g in groupby(d, lambda x: 1 if x > 0 else -1 if x < 0 else 0):
            s = [0] + list(map(abs, g))
            r += sum(max(b - a, 0) for a, b in zip(s, s[1:]))
        return r
