/**
 * @param {number[]} nums
 * @param {number[]} target
 * @return {number}
 */
var minimumOperations = function(nums, target) {
    const diff = nums.map((num, i) => target[i] - num);
    let totalOperations = 0;
    let currentSegmentDiff = 0;
    for (let i = 0; i < diff.length; i++) {
        if (i === 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
            const additionalOperations = Math.abs(diff[i]) - Math.abs(currentSegmentDiff);
            if (additionalOperations > 0) {
                totalOperations += additionalOperations;
            }
            currentSegmentDiff = diff[i];
        } else {
            totalOperations += Math.abs(diff[i]);
            currentSegmentDiff = diff[i];
        }
    }
    return totalOperations;
};
