class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> vec(n);
        for(int i=0;i<n;i++){
            vec[i]=target[i]-nums[i];
        }
        long long op=0,curr=0;
        for(int i=0;i<n;i++){
            if(i==0 || (vec[i-1]>0 &&vec[i]>0)||(vec[i-1]<0 && vec[i]<0)){
                long long temp=abs(vec[i])-abs(curr);
                if(temp>0){
                    op+=temp;
                }
            }
            else{
                op+=abs(vec[i]);
            }
            curr=vec[i];
        }
        return op;
    }
};
