long long f(vector<int>& v, int l, int r, int t) {
	long long ans = 0;
	int t0;
	if (v[l] > 0) {
		t0 = *min_element(v.begin() + l, v.begin() + r + 1);
		ans += t0 - t;
	}
	else {
		t0 = *max_element(v.begin() + l, v.begin() + r + 1);
		ans += t - t0;
	}
	int l0 = l;
	for (int i = l; i <= r; i++) {
		if (v[i] == t0) {
			if (i > l && v[i - 1] != t0)
				ans += f(v, l0, i - 1, t0);
			l0 = i + 1;
		}
	}
	if (l0 <= r)
		ans += f(v, l0, r, t0);
	return ans;
}

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
		int n = nums.size();
		long long ans = 0;
		vector<int> v(n);
		for (int i = 0; i < n; i++)
			v[i] = nums[i] - target[i];
		int l = 0;
		for (int i = 0; i < n; i++) {
			if (i && (v[i] >= 0 && v[i - 1] < 0 || v[i] <= 0 && v[i - 1] > 0)) {
				ans += f(v, l, i - 1, 0);
				l = i;
			}
			if (v[i] == 0)
				l = i + 1;
		}
		if (l < n)
			ans += f(v, l, n - 1, 0);
		return ans;
    }
};
