class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int len = nums.length;
        int[] diff = new int[len];
        for (int i = 0; i < len; i++){
            diff[i] = target[i]-nums[i];
        }
        int index = 0;
        long res = 0;
        while (index < len){
            if (diff[index] > 0){
                int prevDiff = diff[index];
                while (index < len && diff[index] > 0){
                    if (diff[index] >= prevDiff){
                        prevDiff = diff[index]; 
                    } else {
                        res += prevDiff - diff[index];
                        prevDiff = diff[index];
                    }
                    index++;
                }
                res += prevDiff;
            } else if (diff[index] < 0){
                int prevDiff = diff[index];
                while (index < len && diff[index] < 0){
                    if (diff[index] <= prevDiff){
                        prevDiff = diff[index]; 
                    } else {
                        res += diff[index] - prevDiff;
                        prevDiff = diff[index];
                    }
                    index++;
                }
                res -= prevDiff;
            } else {
                index++;
            }
        }
        return res;
    }
}
