class Solution {
    long long solve(vector<int>& v) {
        int len = v.size();

        if (v[0] < 0) {
            for (int i = 0; i < len; i++) {
                v[i] *= -1;
            }
        }

        map<int, vector<int>> m;

        for (int i = 0; i < len; i++) {
            m[v[i]].push_back(i + 1);
        }

        vector<bool> vis(len + 2, false);
        vis[0] = true;
        vis[len + 1] = true;
        long long ans = 0;
        int segments = 1;
        int cur_min = 0, cur_val;

        while (!m.empty()) {
            auto it = m.begin();
            cur_val = it->first;
            vector<int> v1 = it->second;
            ans += (long long) (cur_val - cur_min) * (long long) segments;
            int sz = v1.size();

            for (int i = 0; i < sz; i++) {
                if (vis[v1[i] - 1] == false and vis[v1[i] + 1] == false) {
                    segments++;
                    vis[v1[i]] = true;
                    continue;
                }

                if (vis[v1[i] - 1] == true and vis[v1[i] + 1] == true) {
                    segments--;
                }
                vis[v1[i]] = true;
            }
            cur_min = cur_val;
            m.erase(it->first);
        }

        return ans;
    }
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int len = nums.size();
        long long ans = 0;

        vector<int> diffs;
        int diff;

        for (int i = 0; i < len; i++) {
            diff = nums[i] - target[i];

            if (diff == 0) {
                if (diffs.size() > 0) {
                    ans += solve(diffs);
                }

                diffs.clear();

                continue;
            }

            if (diffs.empty()) {
                diffs.push_back(diff);
                continue;
            }

            if(diffs[0] > 0 and diff > 0) {
                diffs.push_back(diff);
                continue;
            }

            if (diffs[0] < 0 and diff < 0) {
                diffs.push_back(diff);
                continue;
            }

            ans += solve(diffs);
            diffs.clear();
            diffs.push_back(diff);
        }

        if (!diffs.empty()) {
            ans += solve(diffs);
        }

        return ans;
    }
};
