using ll = long long;

class Solution {
public:
    ll minimumOperations(vector<int>& a, vector<int>& b) {
        int n = a.size();
        ll x = 0, ans = 0;
        vector<int> c;
        for(int i = 0; i < n; i++){
            c.push_back(a[i] - b[i]);
        }
        ans += abs(c[0]);
        for(int i = 1; i < n; i++){
            if(c[i] >= 0 && c[i - 1] >= 0){
                if(c[i] > c[i - 1]) ans += c[i] - c[i - 1];
            } else if(c[i] <= 0 && c[i - 1] <= 0){
                if(c[i] < c[i - 1]) ans += c[i - 1] - c[i];
            } else {
                ans += abs(c[i]);
            }
        }
        return ans;
    }
};
