class Solution {
    int arr[], tar[];
    long ans = 0;
    int n;
    public long minimumOperations(int[] nums, int[] target) {
        arr = nums;
        tar = target;
        n = nums.length;
        solve(0, n - 1);
        return ans;
    }
    private void solve(int x, int y) {
        if (x < 0 || y >= n || y < x) return;
        if (x == y) {
            ans += Math.abs(arr[x] - tar[y]);
            return;
        }
        boolean d = false;
        boolean in = false;
        int min = Integer.MAX_VALUE;
        for (int i = x; i <= y; i++) {
            if (arr[i] == tar[i]) {
                solve(x, i - 1);
                solve(i + 1, y);
                return;
            } else {
                if (arr[i] < tar[i]) {
                    in = true;
                } else {
                    d = true;
                }
                if (d && in) {
                    solve(x, i - 1);
                    solve(i, y);
                    return;
                }
            }
            min = Math.min(min, Math.abs(arr[i] - tar[i]));
        }
        if (d) {
            min = -min;
        }
        for (int i = x; i <= y; i++) {
            arr[i] += min;
        } 
        ans += Math.abs(min);
        in = false;
        d = false;
        for (int i = x; i <= y; i++) {
            if (arr[i] == tar[i]) {
                solve(x, i - 1);
                solve(i + 1, y);
                return;
            } else {
                if (arr[i] < tar[i]) {
                    in = true;
                } else {
                    d = true;
                }
                if (d && in) {
                    solve(x, i - 1);
                    solve(i, y);
                    return;
                }
            }
        }
    }
}
