class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size(),c=0;
        long long ans=0;
        vector<int>d(n);
        for(int i=0;i<n;i++)
        d[i]=target[i]-nums[i];
        for(int i=0;i<n;i++){
            if(i==0||(d[i-1]>0&&d[i]>0)||(d[i-1]<0&&d[i]<0)){
                int x=abs(d[i])-abs(c);
                if(x>0)ans+=x;
            }
            else
            ans+=abs(d[i]);
            c=d[i];
        }
        return ans;
    }
};
