class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) 
    {

        long long n=nums.size();
        long long ans=0;
        vector<long long> diff(n);
        for(int i=0;i<n;i++){
            diff[i]=(target[i]-nums[i]);
        }
        priority_queue<int,vector<int>,greater<int>> pq;
        int i=0;
        int j=0;
        long long prev=0;
        while(j<n)
        { 
            while(j<n&&diff[j]>0)
            {
             if(j<n&&diff[j]>prev){
                 ans+=(diff[j]-prev);

             }
            prev=diff[j];
            j++;

            }
            prev=0;        
            while(j<n&&diff[j]<=0)
            {
                if(j<n&&diff[j]<prev){
                    ans+=(prev-diff[j]);

                }
            prev=diff[j];
            j++;

            }
            prev=0;

        }
        return ans;


    }
};
