#define lli long long int

class Solution {
public:
    lli findPos(vector<int>& nums, lli start, lli end){
        if(start>end || start<0 || end>=nums.size()) return 0;
        lli cnt = *min_element(nums.begin()+start,nums.begin()+end+1);
        for(lli i=start; i<=end; i++){
            nums[i] = nums[i] - cnt;
        }
        lli ans = cnt;
        for(lli i=start; i<=end; i++){
            if(nums[i]>0){
                lli j=i; 
                while(j<=end && nums[j]>0){
                    j++;
                }

                if(j!=i) ans += findPos(nums, i, j-1);

                while(j<=end && nums[j]<=0) j++;

                i = j-1;
            }
        }

        return ans;
    }

    lli findNeg(vector<int>& nums, lli start, lli end){
        if(start>end || start<0 || end>=nums.size()) return 0;
        lli cnt = (-1)*(*max_element(nums.begin()+start,nums.begin()+end+1));
        for(lli i=start; i<=end; i++){
            nums[i] = nums[i] + cnt;
        }
        lli ans = cnt;
        for(lli i=start; i<=end; i++){
            if(nums[i]<0){
                lli j=i; 
                while(j<=end && nums[j]<0){
                    j++;
                }
                if(j!=i) ans += findNeg(nums, i, j-1);

                while(j<=end && nums[j]>=0) j++;

                i = j-1;
            }
        }

        return ans;
    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        lli ans = 0;
        lli n = nums.size();
        for(lli i=0; i<n; i++){
            nums[i] = target[i]-nums[i];
        }

        for(lli i=0; i<n; i++){
            if(nums[i]>0){
                lli j = i;
                while(j<n && nums[j]>0){
                    j++;
                }
                j--;
                ans += findPos(nums, i, j);
                i = j;
            }
            else if(nums[i]<0){
                lli j = i;
                while(j<n && nums[j]<0){
                    j++;
                }
                j--;
                ans += findNeg(nums, i, j);
                i = j;
            }
        }
        return ans;
    }
};
