#define ll long long int
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<ll>diff(n);
        for(int i=0;i<(int)nums.size();i++){
            ll d=nums[i]-target[i];
            diff[i]=d;
        }
        vector<ll>result;
        int flag=0;
        if(diff[0]>0){
            flag=1;
        }
        for(ll i=0;i<n;i++){
           if(diff[i]==0){
               if(i<n-1){
                   if(diff[i+1]<0){
                       flag=0;
                   }else if(diff[i+1]>0){
                       flag=1;
                   }
               }
               continue;
           }
            vector<ll>temp;
           if(flag){

               while(i<n&&diff[i]>0){
                   temp.push_back(abs(diff[i]));
                   i++;
               }
               i--;
               flag=0;

           }else{
               while(i<n&&diff[i]<0){
                   temp.push_back(abs(diff[i]));
                   i++;
               }
               i--;
               flag=1;
           }
            ll mx=temp[0];
            for(ll j=1;j<(ll)temp.size();j++){
                if(temp[j]<temp[j-1]){
                    continue;
                }else{
                    mx+=(temp[j]-temp[j-1]);
                }
            }

            result.push_back(mx);

        }
        // for(auto it:result){
        //     cout<< it << " ";
        // }cout << endl;
        ll ans=accumulate(result.begin(),result.end(),0ll);
        return ans;
    }
};
