class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            nums[i] -= target[i];
            // cout << nums[i] << " ";
        }
        int sign = 0;
        bool inc;
        int mx = 0;
        long long res = 0;
        vector<int> mxIs;
        int mxI;
        for (int i = 0; i < n; i++) {
            // cout << i << " ";
            if (sign == 0) {
                if (nums[i] == 0) continue;
                if (nums[i] > 0) sign = 1;
                else sign = -1;
                inc = true;
                mx = abs(nums[i]);
                mxI = i;
            } else if (sign == 1) {
                if (nums[i] <= 0) {
                    res += mx;
                    mxIs.push_back(mxI);
                    sign = 0;
                    if (nums[i] < 0) i--;
                } else {
                    if (nums[i] > nums[i - 1]) {
                        if (!inc) {
                            res += mx;
                            mxIs.push_back(mxI);
                            sign = 0;
                            i--;
                        } else {
                            mx = nums[i];
                            mxI = i;
                        }
                    } else if (nums[i] < nums[i - 1]) {
                        if (inc) inc = false;
                    }
                } 
            } else {
                 if (nums[i] >= 0) {
                    res += mx;
                    mxIs.push_back(mxI);
                    sign = 0;
                    if (nums[i] > 0) i--;
                } else {
                    if (nums[i] < nums[i - 1]) {
                        if (!inc) {
                            res += mx;
                            mxIs.push_back(mxI);
                            sign = 0;
                            i--;
                        } else {
                            mx = -nums[i];
                            mxI = i;
                        }
                    } else if (nums[i] > nums[i - 1]) {
                        if (inc) inc = false;
                    }
                }
            }
        }
        if (sign != 0) {
            res += mx;
            mxIs.push_back(mxI);
        }
        mxI = mxIs.size();
        int mn;
        // cout << endl;
        // for (int i : mxIs) cout << i << " ";
        // cout << endl;
        for (int i = 1; i < mxI; i++) {
            // cout << mxIs[i - 1] << " " << mxIs[i] << ": ";
            if ((nums[mxIs[i - 1]] < 0 && nums[mxIs[i]] > 0) || (nums[mxIs[i - 1]] > 0 && nums[mxIs[i]] < 0)) {
                // cout << endl;
                continue;
            }
            mn = abs(nums[mxIs[i - 1]]);
            for (int j = mxIs[i - 1] + 1; j <= mxIs[i]; j++) {
                // cout << nums[j] << " ";
                if (abs(nums[j]) < mn) mn = abs(nums[j]);
            }
            // cout << " | " << mn << endl;
            res -= mn;
        }
        return res;
    }
};
