class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int i,n=a.size();
        for(i=0;i<n;i++)b[i]-=a[i];
        long long ans=abs(b[0]),temp=b[0];
        for (i=1;i<n; i++) {
            int x=abs(temp);
            if(b[i-1]<0 && b[i]<0){
                if (abs(b[i])-x>0) {
                    ans+=abs(b[i])-x;
                }
            } 
            else if(b[i-1]>0 && b[i]>0){
                if (b[i]-x>0) {
                    ans+=b[i]-x;
                }
            }
            else ans += abs(b[i]);
            temp=b[i];
        }

        return ans;
    }
};
