#define ll long long
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll n=nums.size();
        vector<ll> v(n);
        for(int i=0;i<n;i++){
            // v[i]=abs(target[i]-nums[i]);
            v[i]=target[i]-nums[i];
        }
        ll x=0;
        ll ans=abs(v[0]);
        ll diff=abs(v[0]);
        if(v[0]>=0) x=1;
        for(int i=1;i<n;i++){
            if(v[i]<0&&x==1){
                diff=abs(v[i]);
                ans+=diff;
                x=0;
                continue;
            }
            if(v[i]>=0&&x==0){
                diff=abs(v[i]);
                ans+=diff;
                x=1;
                continue;
            }
            if(diff>abs(v[i])){
                diff=abs(v[i]);
                continue;
            }else{
                ans+=abs(v[i])-diff;
                diff=abs(v[i]);   
            }
        }
        return ans;
    }
};
