using i64 = long long;

constexpr int MAX = 100010;

i64 a[MAX];

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
      int n = nums.size();
      for (int i = 0; i < n; ++i) {
        a[i] = target[i] - nums[i];
      }
      i64 pre = 0, ret = 0;
      for (int i = 0; i < n; ++i) {
        if (a[i] < 0) {
          ret += max(0LL, min(0LL, pre) - a[i]);
        } else {
          ret += max(0LL, a[i] - max(0LL, pre));
        }
        pre = a[i];
      }
      return ret;
    }
};
