class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        originalDifferences=[]
        for i in range(len(nums)):
            originalDifferences.append(nums[i]-target[i])
        differences=[]
        for i in range(len(originalDifferences)):
            if i>0 and originalDifferences[i]==originalDifferences[i-1]:
                continue
            else:
                differences.append(originalDifferences[i])
        #print(originalDifferences)
        #print(differences)
        maxSeen=[]
        currentMax=abs(differences[0])
        for i in range(1,len(differences)):
            if differences[i]==0 and differences[i-1]<0:
                maxSeen.append(currentMax)
                currentMax=float('-inf')
            elif differences[i]==0 and differences[i-1]>0:
                maxSeen.append(currentMax)
                currentMax=float('-inf')
            elif differences[i]>0 and differences[i-1]<0:
                maxSeen.append(currentMax)
                currentMax=float('-inf')
            elif differences[i]<0 and differences[i-1]>0:
                maxSeen.append(currentMax)
                currentMax=float('-inf')
            if differences[i]<0:
                if i<(len(differences)-1) and differences[i-1]<differences[i]>differences[i+1]:
                    maxSeen.append(currentMax+differences[i])
                    currentMax=abs(differences[i])
                currentMax=max(currentMax, abs(differences[i]))
            if differences[i]>0:
                if i<(len(differences)-1) and differences[i-1]>differences[i]<differences[i+1]:
                    maxSeen.append(currentMax-differences[i])
                    currentMax=differences[i]
                currentMax=max(currentMax, abs(differences[i]))
        print(differences)
        print(currentMax)
        if differences[-1]!=0:
            maxSeen.append(currentMax)
        return sum(maxSeen)
