class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long r=0;
        long long ans=0;
        long long n = nums.size();
        while(r<n){
            long long currDiff=target[r]-nums[r];
            long long prevDiff = 0;
            if(currDiff>=0){
                while(currDiff>=0){
                    if(currDiff-prevDiff>=0){
                        ans+=currDiff-prevDiff;
                    }
                    prevDiff=currDiff;
                    r++;
                    if(r<n){
                        currDiff=target[r]-nums[r];
                    }
                    else{
                        break;
                    }
                }
            }
            else{
                while(currDiff<=0){
                    if(currDiff-prevDiff<=0){
                        ans+=prevDiff-currDiff;
                    }
                    prevDiff=currDiff;
                    r++;
                    if(r<n){
                        currDiff=target[r]-nums[r];
                    }
                    else{
                        break;
                    }
                }
            }
        }
        return ans;
    }
};
