class Solution {
    public long minimumOperations(int[] nums, int[] target) {
          int n = nums.length;
        int[] diff = new int[n];
        for(int i=0;i<nums.length;i++){
            diff[i]=nums[i]-target[i];
        }
           long to = 0;
        int cd = 0;

        for (int i = 0; i < n; i++) {
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                int ap = Math.abs(diff[i]) - Math.abs(cd);
                if (ap > 0) {
                    to += ap;
                }
                cd = diff[i];
            } else {
                to += Math.abs(diff[i]);
                cd = diff[i];
            }
        }

        return to;
    }
}
