class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int>diff(nums.size(),0);
        for(int i=0;i<nums.size();i++){
            diff[i]=target[i]-nums[i];
        }
        bool is_positive=true;
        int right=0;
        int maxi=0;
        long long times=0;
        int mini=0;
        while(right<diff.size()){
            if(diff[right]>0 && is_positive){
                if(diff[right]<maxi){
                    times+=abs(diff[right]-maxi);
                    maxi=diff[right];
                }
                else{
                    maxi=max(maxi,diff[right]);
                }
            }
            else if(diff[right]<0 && is_positive){
                is_positive=false;
                times+=maxi;
                maxi=abs(diff[right]);
            }
            else if(diff[right]>0 && !is_positive){
                is_positive=true;
                times+=maxi;
                maxi=abs(diff[right]);
            }
            else if(diff[right]<0 && !is_positive){
                if(abs(diff[right])<maxi){
                    times+=abs(abs(diff[right])-maxi);
                    maxi=abs(diff[right]);
                }
                else{
                    maxi=max(maxi,abs(diff[right]));
                }
            }
            else if(diff[right]==0){
                times+=maxi;
                maxi=0;
            }
            right++;
        }
        times+=maxi;
        return times;
    }
};
