class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diff;
        long long res = 0;

        for(int i=0;i<nums.size();i++)
            diff.push_back(nums[i] - target[i]);

        for(int i=0;i<diff.size();)
        {
            // printf("\n");
            // for(auto &a : diff)
            //     printf("%d ", a);

            if(diff[i] < 0)
            {
                int l = 1, m = INT_MIN, tmp = i;
                while (tmp+1<diff.size()&& diff[tmp+1] < 0)
                {

                    m = max(m, max(diff[tmp], diff[tmp+1]));
                    // printf("\n%d %d %d ", diff[tmp], diff[tmp+1], m);
                    l++;
                    tmp++;
                }
                if (l == 1)
                {
                    res += abs(diff[i]);
                    diff[i] = 0;
                }
                else
                {
                    // printf("    i = %d l = %d m = %d\n", i,l,m);
                    for(int j=0;j<l;j++)
                    {
                        diff[i+j] += abs(m);
                    }
                    res += abs(m);
                }
            }

            else if(diff[i] > 0)
            {
                int l = 1, m = INT_MAX, tmp = i;
                while (tmp+1<diff.size()&& diff[tmp+1] > 0)
                {
                    m = min(m, min(diff[tmp], diff[tmp+1]));
                    l++;
                    tmp++;
                }
                if (l == 1)
                {
                    res += abs(diff[i]);
                    diff[i] = 0;
                }
                else
                {
                    for(int j=0;j<l;j++)
                    {
                        diff[i+j] -= abs(m);
                    }
                    res += abs(m);
                }
            }
            else
                i++;
        }
        if (diff.back() != 0)
            res += abs(diff.back());
        return res;
    }
};
