class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        diff = [0] * n
        for i in range(n):
            diff[i] = target[i] - nums[i]

        # print (diff)
        def count(l, r):
            # print (l, r, diff)
            if l > r:
                return 0
            if l == r:
                cnt = abs(diff[l])
                diff[l] = 0
                return cnt

            min_i = l
            for i in range(l + 1, r + 1):
                if abs(diff[i]) < abs(diff[min_i]):
                    min_i = i

            min_val = diff[min_i]
            cnt = abs(diff[min_i])

            for i in range(l, r + 1):
                diff[i] -= min_val

            i = l
            while i <= r:
                if diff[i] == 0:
                    i += 1
                    continue
                elif diff[i] > 0:
                    j = i
                    while j + 1 < n and diff[j + 1] > 0:
                        j += 1
                    cnt += count(i, j)
                    i = j + 1 
                else:
                    j = i
                    while j + 1 < n and diff[j + 1] < 0:
                        j += 1
                    cnt += count(i, j)
                    i = j + 1 

            return cnt

        ans = 0
        i = 0
        while i < n:
            if diff[i] == 0:
                i += 1
                continue
            elif diff[i] > 0:
                j = i
                while j + 1 < n and diff[j + 1] > 0:
                    j += 1
                ans += count(i, j)
                i = j + 1
            else:
                j = i
                while j + 1 < n and diff[j + 1] < 0:
                    j += 1
                ans += count(i, j)
                i = j + 1

        return ans
