class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diffs = [nums[i]-target[i] for i in range(len(nums))]
        print(diffs)
        ans = 0
        last = 0
        for n in diffs:
            if n > 0:
                if last > 0:
                    if n > last:
                        ans += n-last
                else:
                    ans += n
            if n < 0:
                if last < 0:
                    if n < last:
                        ans += last-n
                else:
                    ans -= n
            last = n
        return ans
