class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long n = nums.size();
        long long i = 0;
        long long ans = 0;
        long long count = 0;
        vector<long long> value(n, 0);
        while (i < n) {
            value[i] = target[i] - nums[i];
            i++;
        }
        i = 0;
        while (i < n) {
            if (i == 0) {
                long long diff = abs(value[i]) - abs(count);
                if (diff > 0) {
                    ans = ans + diff;
                }
                count = value[i];
            } else if ((value[i - 1] > 0 && value[i] > 0)) {
                long long diff = abs(value[i]) - abs(count);
                if (diff > 0) {
                    ans = ans +  diff;
                }
                count = value[i];
            } else if ((value[i - 1] < 0 && value[i] < 0)) {
                long long diff = abs(value[i]) - abs(count);
                if (diff > 0) {
                    ans = ans + diff;
                }
                count = value[i];
            } else {
                ans = ans + abs(value[i]);
                count = value[i];
            }
            i++;
        }
        return ans;
    }
};
