class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long ans = 0;
        vector<int> diff;
        for(int i = 0; i < n; ++i){
            diff.push_back(nums[i]-target[i]);
        }

        vector<vector<int>> v;
        for(int i = 0; i < n; ++i){
            int val = diff[i];
            if(val == 0) continue;
            vector<int> temp; temp.push_back(abs(val));
            i++;
            while(i < n && val > 0 && diff[i] > 0) temp.push_back(diff[i++]);
            while(i < n && val < 0 && diff[i] < 0) temp.push_back(abs(diff[i++]));
            if(temp.size())
            v.push_back(temp);
            i--;
        }
        // for(auto i : v){
        //     for(auto j : i) cout << j << " ";
        //     cout << endl;
        // }
        for(auto it : v){
            long long sub = 0, maxi = 0;
            for(int i = 0; i < it.size(); ++i){
                if(i > 0 && it[i] < it[i-1]){
                    ans += max((long long)0, (maxi-sub));
                    if(it[i] < sub) sub = it[i];
                    else
                    sub += max((long long)0, (long long)it[i] - sub);
                    maxi = 0;
                }
                else{
                    maxi = max(maxi, (long long)it[i]);
                }
            }
            if(maxi) ans += max((long long)0, (maxi-sub));
        }
        return ans;
    }
};
