class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> difference(nums.size(), 0);
        for (int i = 0; i < nums.size(); i++)
            difference[i] = target[i] - nums[i];

        long long answer = 0;
        for (int i = 0; i < nums.size(); i++)
        {
            if (difference[i] == 0)
                continue;
            if (difference[i] > 0)
            {
                int r = i;
                while (r + 1 < nums.size() && difference[r+1] > 0)
                    r++;
                answer += calculate(difference, i, r);
                i = r;
            }
            if (difference [i] < 0)
            {
                int r = i;
                while (r + 1 < nums.size() && difference[r+1] < 0)
                    r++;
                answer += calculate(difference, i, r);
            }
        }

        return answer;
    }
private:
    long long calculate(vector<int> &difference, int l, int r)
    {
        if (difference[l] < 0)
        for (int i = l; i <= r; i++)
            difference[i] = -difference[i];
        long long minimum = INT_MAX;
        for (int i = l; i <= r; i++)
            minimum = min(minimum, (long long)difference[i]);

        for (int i = l; i <= r; i++)
            difference[i] -= minimum;

        for (int i = l; i <= r; i++)
        {
            if (difference[i])
            {
                int new_r = i;
                while (new_r + 1 <= r && difference[new_r+1])
                    new_r++;
                minimum += calculate(difference, i, new_r);
                i = new_r;
            }

        }

        return minimum;
    }
};
