class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        int[] dd = new int[n];
        int[] d = new int[n];
        for (int i = 0; i < n; i++) {
            dd[i] = target[i] - nums[i];
        }
        long p = 0;
        long q = 0;
        for (int i = n - 1; i >= 1; i--) {
            d[i] = dd[i] - dd[i - 1];
            if (d[i] > 0) {
                p += d[i];
            } else {
                q -= d[i];
            }
        }
        d[0] = dd[0];
        if (d[0] > 0) {
            p += d[0];
        } else {
            q -= d[0];
        }

        return Math.max(p, q);
    }
}
