class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long n  = nums.size();
        vector<long long> v(n, 0);
        for(int i = 0; i<n; i++){
            v[i] = target[i]-nums[i];
        }
        long long ans = 0;
        int j = 0;
        for(int i = 0; i<n;){
            long long val = v[i] , j = i;
            while(j<n && ((val>=0 && v[j]>=0) || (val<0 && v[j]<0)))j++;
            ans+=abs(v[i]);

            for(int k = i; k<j ;k++){
                long long it = abs(v[k]);
                if(k-1>=i && abs(v[k])>abs(v[k-1])) ans+= abs(v[k])-abs(v[k-1]);
            }
            i=j;
        }
        return ans ;
    }
};
