class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long ans = 0;
        int n = nums.length;
        int[] dif = new int[n];
        for(int i=0;i<n;i++){
            dif[i] = target[i]-nums[i];
        }

        for(int i=0;i<n;i++){
            if(dif[i]!=0){
                int val = Integer.MAX_VALUE;
                if(dif[i] > 0){
                    int j = i;
                    while(j<n && dif[j] > 0){
                        val = Math.min(val,dif[j]);
                        j++;
                    }

                    for(int k=i;k<j;k++){
                        dif[k] -= val;
                    }
                    ans += val;
                    i--;

                }
                else{
                    int j = i;
                    while(j<n && dif[j] < 0){
                        val = Math.min(val,Math.abs(dif[j]));
                        j++;
                    }

                    for(int k=i;k<j;k++){
                        dif[k] += val;
                    }
                    ans += val;
                    i--;
                }
            }
        }
        return ans;
    }
}
