class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        d = nums[0] - target[0]
        ans = 0
        for i in range(1, n):
            x, y = nums[i], target[i]
            nd = x - y
            if nd == d:
                continue
            if d == 0:
                d = nd
                continue
            if nd == 0:
                ans += abs(d)
                d = 0
                continue
            if d * nd > 0:
                if d > 0:
                    if d > nd:
                        ans += abs(d - nd)
                        d = nd
                    else:
                        d = nd
                else:
                    if d > nd:
                        d = nd
                    else:
                        ans += abs(d - nd)
                        d = nd
            else:
                ans += abs(d)
                d = nd
        ans += abs(d)
        return ans
