class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> diff(n);
        for(int i=0; i<nums.size(); i++){
            diff[i] = (long long)target[i] - (long long)nums[i];
        }
        long long ans = 0;
        for(int i=0; i<n; i++){
            if(diff[i] > 0){
                ans += diff[i];
                i++;
                while(i < n && diff[i] > 0){
                    ans += max(diff[i]-diff[i-1],0LL);
                    i++;
                }
                i--;
            }
            else if(diff[i] < 0){
                ans += abs(diff[i]);
                i++;
                while(i < n && diff[i] < 0){
                    ans += max(diff[i-1]-diff[i],0LL);
                    i++;
                }
                i--;
            }
        }
        return ans;
    }
};
