class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        diffs = [0] * n

        res = 0

        q = deque([])

        for i in range(n):
            diffs[i] = target[i] - nums[i]


        def solve(l,r, diff):
            nonlocal res

            if l > r:
                return
            minPos = []
            minVal = abs(diffs[l])
            for i in range(l,r+1):
                if minVal == abs(diffs[i]):
                    minPos.append(i)
                    continue
                if minVal > abs(diffs[i]):
                    minVal = abs(diffs[i])
                    minPos = [i]

            res += minVal - diff

            for v in minPos:
                solve(l, v-1, minVal)
                l = v+1

            solve(l, r, minVal)

        l = 0
        for i in range(n):
            if diffs[l] > 0:
                if diffs[i] > 0:
                    continue
                else:
                    solve(l, i-1, 0)
                    l = i
            elif diffs[l] < 0:
                if diffs[i] < 0:
                    continue
                else:
                    solve(l, i-1, 0)
                    l = i
            else:
                l += 1

        solve(l, n-1, 0)


        return res
