class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n);
        for (int i = 0; i < n; ++i) {
            diff[i] = target[i] - nums[i];
        }

        long long ans = 0;
        long long current = 0;
        bool isPositive=false;

        for (int i = 0; i < n; ++i) {
            if (i == 0 || (isPositive && diff[i] > 0) || (!isPositive && diff[i] < 0)) {
                int temp =abs(diff[i]) - abs(current);
                ans += max(0,temp);
                current = diff[i];
                if(diff[i]>0) isPositive=true; 
                else isPositive=false;
            }  else {
                ans += abs(diff[i]);
                current = diff[i];
                if(diff[i]>0) isPositive=true; 
                else isPositive=false;
            }
        }

        return ans;
    }
};
