class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();

        vector<int> dif;
        dif.push_back(target[0]-nums[0]);

        for(int i = 1; i < n; ++i){
            dif.push_back((target[i]-nums[i]) - (target[i-1]-nums[i-1]));
        }

        long long pos = 0, neg = 0;
        for(int v: dif){
            if(v > 0) pos += v;
            else neg += -v;
            // cout << v << " ";
        }

        return max(pos, neg);
    }
};
